<template>
  <div class="emfe-panel-c">
    <div class="emfe-panel-c-main">
      <div class="emfe-panel-c-main-logobox">
        <emfe-upload className="emfe-panel-c-main" :action="uploadAction" @success="uploadSuccess" :url="headImg"></emfe-upload>
      </div>
      <div class="emfe-panel-c-main-infobox">
        <div class="emfe-panel-c-main-infobox-about">
        <span class="emfe-panel-c-main-infobox-about-texts"><i class="emfe-panel-c-main-infobox-about-texts-i">{{greeting}}
        </i>，{{showNickName}}</span>
          <span class="emfe-panel-c-main-infobox-about-father">
          <div class="emfe-panel-c-main-infobox-member" v-if="openMember===1 || openVip===1">
          <span class="emfe-panel-c-main-infobox-about-text">
          <img class="emfe-panel-c-main-infobox-about-novip" :src="memberlogoNoVip" v-if="isVip===2&&openVip===1" @click="renews">
          <img class="emfe-panel-c-main-infobox-about-img" v-if='!(openMember===2 &&openVip===1&&isVip===2)' :src="memberlogo" @click="freeLevels"></span>
          <a class="emfe-panel-c-main-infobox-youhui" v-if="isVip===2&&openVip===1&&timeJudge===''">开通会员享优惠 !<i
                  class="emfe-panel-c-main-infobox-youhui-i"></i></a>
        </div>
        <template v-if="openMember===1 || openVip===1">
          <span v-if="isVip===2&&openVip===1&&timeJudge===1" class="emfe-panel-c-main-infobox-about-overdue">{{
              memberDeadlines}}到期啦 !<i
                    class="emfe-panel-c-main-infobox-about-overdue-i"></i>
          </span>
          <emfe-link class="emfe-panel-c-main-infobox-about-link" :routers="{}" v-if="openVip===1&&isVip===1 || timeJudge===1" @click="renews">续费</emfe-link>
          <!--<span class="emfe-panel-c-main-infobox-about-text" v-if="(openVip===1&&isVip===2&&openMember===1) || openMember===1&&isVip===2">成长值<em class="emfe-panel-c-main-infobox-about-text-em">{{ growth }}</em></span>-->
          <!--<span class="emfe-panel-c-main-infobox-about-text" v-if="openVip===1&&isVip===1 || openMember===1">积分<em class="emfe-panel-c-main-infobox-about-text-em">{{ integral }}</em></span>-->
        </template>
        </span>
        </div>
        <div class="emfe-panel-c-main-infobox-info">
          <span class="emfe-panel-c-main-infobox-info-text">账号 : (+{{code}}){{showPhone}}</span>
          <!-- <emfe-link className="emfe-panel-c-main" :routers="telRouters">
            <emfe-icon type="shouji1" className="emfe-panel-c-main"></emfe-icon>
          </emfe-link>
          <emfe-link className="emfe-panel-c-main" :routers="infoRouters">
            <emfe-icon type="chengwei1" className="emfe-panel-c-main"></emfe-icon>
          </emfe-link>
          <emfe-link className="emfe-panel-c-main" :routers="walletRouters">
            <emfe-icon type="zhongzhimima" className="emfe-panel-c-main"></emfe-icon>
          </emfe-link> -->
        </div>
      </div>
      <div class="emfe-panel-c-main-orderbox">
        <div class="emfe-panel-c-main-orderbox-item" @click="pendingPays">
          <img src="data:image/svg+xml;base64," class="emfe-panel-c-main-orderbox-img">
          <p class="emfe-panel-c-main-orderbox-text">待支付 ({{pendingPaymentNum}}) </p>
        </div>
        <div class="emfe-panel-c-main-orderbox-item" @click="pendingAudits">
          <img src="data:image/svg+xml;base64," class="emfe-panel-c-main-orderbox-img">
          <p class="emfe-panel-c-main-orderbox-text">待审核 ({{pendingAuditNum}}) </p>
        </div>
        <div class="emfe-panel-c-main-orderbox-item" @click="pendingDelivered">
          <img src="data:image/svg+xml;base64," class="emfe-panel-c-main-orderbox-img">
          <p class="emfe-panel-c-main-orderbox-text">待发货 ({{pendingShipmentNum}}) </p>
        </div>
        <div class="emfe-panel-c-main-orderbox-item" @click="pendingShipments">
          <img src="data:image/svg+xml;base64," class="emfe-panel-c-main-orderbox-img">
          <p class="emfe-panel-c-main-orderbox-text">待收货 ({{pendingReceiveNum}}) </p>
        </div>
      </div>
      <div class="emfe-panel-c-main-border"></div>
      <div class="emfe-panel-c-main-others" :class="{ 'emfe-panel-c-main-othersTwo': (!(openVip===1&&isVip===1 ||
      openMember===1)&& !((openVip===1&&isVip===2&&openMember===1) ||
      openMember===1&&isVip===2)) }">
      <span class="emfe-panel-c-main-others-text">
        <img @click="couponClick"
             src="data:image/svg+xml;base64," class="emfe-panel-c-main-others-text-img">
        优惠券<em class="emfe-panel-c-main-others-text-em">: {{ couponNums }}张</em></span>
        <span class="emfe-panel-c-main-others-text" v-if="openVip===1&&isVip===1 || openMember===1">
        <img @click="integalClick"
             src="data:image/svg+xml;base64," class="emfe-panel-c-main-others-text-img">
        积&nbsp;&nbsp;&nbsp;分<em class="emfe-panel-c-main-others-text-em">: {{ integrals }}</em></span>
        <span class="emfe-panel-c-main-others-text">
        <img @click="balanceClick"
             src="data:image/svg+xml;base64," class="emfe-panel-c-main-others-text-img">
        &nbsp;余&nbsp;&nbsp;&nbsp;额<em class="emfe-panel-c-main-others-text-em">: ￥{{ balances }}</em></span>
        <span class="emfe-panel-c-main-others-text" v-if="(openVip===1&&isVip===2&&openMember===1) ||
      openMember===1&&isVip===2">
        <img @click="growthClick"
             src="data:image/svg+xml;base64," class="emfe-panel-c-main-others-text-img">
        成长值<em class="emfe-panel-c-main-others-text-em">: {{ growths }}</em></span>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  name: 'EmfePanelC',
  data() {
    return {
      logo: '',
      isguoqi: '',
    };
  },
  props: {
    headImg: {
      type: String,
      required: true,
    },
    memberlogo: {
      type: String,
      required: true,
    },
    memberlogoNoVip: {
      type: String,
      required: true,
    },
    nickName: {
      type: String,
      required: true,
    },
    levelName: {
      type: String,
      required: true,
    },
    code: {
      type: Number,
      required: true,
    },
    phone: {
      type: Number,
      required: true,
    },
    isMember: {
      type: Number,
      required: true,
    },
    openMember: {
      type: Number,
      requireed: true,
    },
    isVip: {
      type: Number,
      required: true,
    },
    openVip: {
      type: Number,
      required: true,
    },
    memberDeadline: {
      type: String,
      required: true,
    },
    growth: {
      type: Number,
      required: true,
    },
    integral: {
      type: Number,
      required: true,
    },
    couponNum: {
      type: Number,
      required: true,
    },
    balance: {
      type: Number,
      required: true,
    },
    pendingPaymentNum: {
      type: Number,
      required: true,
    },
    pendingReceiveNum: {
      type: Number,
      required: true,
    },
    pendingAuditNum: {
      type: Number,
      required: true,
    },
    pendingShipmentNum: {
      type: Number,
      required: true,
    },
    telRouters: {
      type: Object,
      required: true,
    },
    infoRouters: {
      type: Object,
      required: true,
    },
    walletRouters: {
      type: Object,
      required: true,
    },
    uploadAction: {
      type: String,
      required: true,
    },
  },
  methods: {
    toggle(status) {
      this.$emit('switch-toogle', status);
    },
    iconClick(index) {
      this.$emit('icon-click', index);
    },
    uploadSuccess(res) {
      this.$emit('uploadSuccess', res);
    },
    pendingPays() {
      this.$emit('pendingPay');
    },
    pendingAudits() {
      this.$emit('pendingAudit');
    },
    pendingShipments() {
      this.$emit('pendingShipment');
    },
    pendingDelivered() {
      this.$emit('pendingDelivered');
    },
    renews() {
      this.$emit('renew');
    },
    freeLevels() {
      this.$emit('freeLevel');
    },
    couponClick() {
      this.$emit('couponClick');
    },
    integalClick() {
      this.$emit('integalClick');
    },
    balanceClick() {
      this.$emit('balanceClick');
    },
    growthClick() {
      this.$emit('growthClick');
    },
  },
  computed: {
    classList() {
      return [
        {
          [`${this.className}-panel-c-main`]: !!this.className,
        },
      ];
    },
    boxName() {
      return [
        {
          [`${this.className}-panel-c-main-box`]: !!this.className,
        },
      ];
    },
    leftName() {
      return [
        {
          [`${this.className}-panel-c-main-box-left`]: !!this.className,
        },
      ];
    },
    rightName() {
      return [
        {
          [`${this.className}-panel-c-main-box-right`]: !!this.className,
        },
      ];
    },
    showPhone() {
      return this.phone.toString().replace(/^(\d{3})\d{4}(\d+)/, '$1****$2');
    },
    memberDeadlines() {
      return this.memberDeadline.slice(0, 10);
    },
    timeJudge() {
      const myDate = new Date();
      const endTime =
          `${myDate.getFullYear()}-${(myDate.getMonth() + 1)}-${myDate.getDate()} ${myDate.getHours()}:${myDate.getMinutes()}:${myDate.getSeconds()}`;
      if (this.memberDeadline === '') {
        return '';
      } else if (this.memberDeadline !== '' && new Date(endTime).getTime() > new Date(this.memberDeadline).getTime()) {
        return 1;
      }
      return 2;
    },
    showNickName() {
      if (this.nickName.length > 5) {
        return `${this.nickName.substring(5, 0)}...`;
      }
      return this.nickName;
    },
    greeting() {
      const myDate = new Date();
      const myhours = myDate.getHours();
      if (myhours >= 0 && myhours < 12) {
        return '早上好';
      } else if (myhours >= 12 && myhours < 13) {
        return '中午好';
      } else if (myhours >= 13 && myhours < 18) {
        return '下午好';
      }
      return '晚上好';
    },
    couponNums() {
      const a = (this.couponNum.toString()).indexOf('.');
      if (a !== -1) {
        if ((this.couponNum.toString()).split('.')[0].length >= 5) {
          return `${(Math.round(this.couponNum) * 0.001).toFixed(1)}k`;
        }
        return this.couponNum;
      }
      if ((this.couponNum.toString()).length >= 5) {
        return `${(this.couponNum) * 0.001}k`;
      }
      return this.couponNum;
    },
    integrals() {
      const a = (this.integral.toString()).indexOf('.');
      if (a !== -1) {
        if ((this.integral.toString()).split('.')[0].length >= 5) {
          return `${(Math.round(this.integral) * 0.001).toFixed(1)}k`;
        }
        return this.integral;
      }
      if ((this.integral.toString()).length >= 5) {
        return `${(this.integral) * 0.001}k`;
      }
      return this.integral;
    },
    balances() {
      const a = (this.balance.toString()).indexOf('.');
      if (a !== -1) {
        if ((this.balance.toString()).split('.')[0].length >= 5) {
          return `${(Math.round(this.balance) * 0.001).toFixed(1)}k`;
        }
        return this.balance;
      }
      if ((this.balance.toString()).length >= 5) {
        return `${(this.balance) * 0.001}k`;
      }
      return this.balance;
    },
    growths() {
      const a = (this.growth.toString()).indexOf('.');
      if (a !== -1) {
        if ((this.growth.toString()).split('.')[0].length >= 5) {
          return `${(Math.round(this.growth) * 0.001).toFixed(1)}k`;
        }
        return this.growth;
      }
      if ((this.growth.toString()).length >= 5) {
        return `${(this.growth) * 0.001}k`;
      }
      return this.growth;
    },
  },
};
</script>
